import axios from 'axios';

// const apiUrl = axios.defaults.baseURL = 'https://localhost:7094/items';
axios.defaults.baseURL = 'https://localhost:7094/items';

// const apiUrl = "https://localhost:7094/items"
axios.interceptors.response.use(function (response) {
  return response;
}, function (error) {
  console.log(error);
  return Promise.reject(error);
});

// Add a 401 response interceptor
window.axios.interceptors.response.use(function (response) {
    return response;
}, function (error) {
    if (401 === error.response.status) {
        swal({
            title: "Session Expired",
            text: "Your session has expired. Would you like to be redirected to the login page?",
            type: "warning",
            showCancelButton: true,
            confirmButtonColor: "#DD6B55",
            confirmButtonText: "Yes",
            closeOnConfirm: false
        }, function(){
            window.location = '/login';
        });
    } else {
        return Promise.reject(error);
    }
});
export default {
  getTasks: async () => {
    const result = await axios.get('/');
    return result.data;
  },

  addTask: async (name) => {
    const result = await axios.post('/', { Name: name, IsComplete: false })
    return result.data;
  },

  setCompleted: async (id, isComplete) => {
    const result = await axios.put(`/${id}?IsComplete=${isComplete}`)
    return result.data;
  },

  deleteTask: async (id) => {
    const result = await axios.delete(`/${id}`);
    return result.data;
  }
};
