import axios from 'axios';
// import swal from 'sweetalert2';
// window.Swal = swal;

// const apiUrl = axios.defaults.baseURL = 'https://localhost:7094/items';

// const apiUrl = "https://localhost:7094/items"
// axios.interceptors.response.use(function (response) {
//   return response;
// }, function (error) {
//   console.log(error);
//   return Promise.reject(error);
// });
import jwt_decode from "jwt-decode";

// axios.defaults.baseURL = process.env.REACT_APP_API_URL;
// console.log('process.env.API_URL', process.env.REACT_APP_API_URL)

axios.defaults.baseURL = 'https://localhost:7094/items';

setAuthorizationBearer();

function saveAccessToken(authResult) {
  localStorage.setItem("access_token", authResult.token);
  setAuthorizationBearer();
}

function setAuthorizationBearer() {
  const accessToken = localStorage.getItem("access_token");
  if (accessToken) {
    axios.defaults.headers.common["Authorization"] = `Bearer ${accessToken}`;
  }
}
axios.interceptors.response.use(
  function(response) {
    return response;
  },
  function(error) {
    if (error.response.status === 401) {
      return (window.location.href = "/login");
    }
    return Promise.reject(error);
  }
);

  export default {
  getTasks: async () => {
    const result = await axios.get('/');
    return result.data;
  },

  addTask: async (name) => {
    const result = await axios.post('/', { Name: name, IsComplete: false })
    return result.data;
  },

  setCompleted: async (id, isComplete) => {
    const result = await axios.put(`/${id}?IsComplete=${isComplete}`)
    return result.data;
  },

  deleteTask: async (id) => {
    const result = await axios.delete(`/${id}`);
    return result.data;
  },
  getLoginUser: () => {
    const accessToken = localStorage.getItem("access_token");
    if (accessToken) {
      return jwt_decode(accessToken);
    }
    return null;
  },

  logout:()=>{
    localStorage.setItem("access_token", "");
  },

  register: async (email, password) => {
    const res = await axios.post("/register", { email, password });
    saveAccessToken(res.data);
  },

  login: async (email, password) => {
    const res = await axios.post("/login", { email, password });
    saveAccessToken(res.data);
  }
};
