import axios from 'axios';

const apiUrl = "https://localhost:7094/items"

export default {
  getTasks: async () => {
    const result = await axios.get(apiUrl);
    return result.data;
  },

    addTask: async (name) => {
      const result = await axios.post(apiUrl, { Name: name, IsComplete: false })
      return result.data;
    },

    setCompleted: async (id, isComplete) => {
      const result = await axios.put(`${apiUrl}/${id}?IsComplete=${isComplete}`)
      return result.data;
    },

    deleteTask: async (id) => {
      const result = await axios.delete(`${apiUrl}/${id}`);
      return result.data;
    }
  };
