import axios from 'axios';

// const apiUrl = axios.defaults.baseURL = 'https://localhost:7094/items';
axios.defaults.baseURL = 'https://localhost:7094/items';

// const apiUrl = "https://localhost:7094/items"
axios.interceptors.response.use(function (response) {
  // Any status code that lie within the range of 2xx cause this function to trigger
  // Do something with response data
  return response;
}, function (error) {
  // Any status codes that falls outside the range of 2xx cause this function to trigger
  // Do something with response error
  return Promise.reject(error);
});
export default {
  getTasks: async () => {
    const result = await axios.get('/');
    return result.data;
  },

    addTask: async (name) => {
      const result = await axios.post('/', { Name: name, IsComplete: false })
      return result.data;
    },

    setCompleted: async (id, isComplete) => {
      const result = await axios.put(`/${id}?IsComplete=${isComplete}`)
      return result.data;
    },

    deleteTask: async (id) => {
      const result = await axios.delete(`/${id}`);
      return result.data;
    }
  };
